mapper.select:
SELECT
,yonghu.yonghu_uuid_number as yonghuUuidNumber ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.yonghu_email as yonghuEmail ,yonghu.new_money as newMoney ,yuangong.yuangong_uuid_number as yuangongUuidNumber ,yuangong.yuangong_name as yuangongName ,yuangong.yuangong_phone as yuangongPhone ,yuangong.yuangong_id_number as yuangongIdNumber ,yuangong.yuangong_photo as yuangongPhoto ,yuangong.yuangong_email as yuangongEmail
FROM shigu a left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id left JOIN yuangong yuangong ON a.yuangong_id = yuangong.id
and a.id in
#{item}
and ( a.yonghu_id = #{params.yonghuId} )
and ( a.yuangong_id = #{params.yuangongId} )
and a.shigu_name like CONCAT('%',#{params.shiguName},'%')
and a.shigu_uuid_number like CONCAT('%',#{params.shiguUuidNumber},'%')
and a.shigu_types = #{params.shiguTypes}
and a.shigu_content like CONCAT('%',#{params.shiguContent},'%')
and a.shigu_delete >= #{params.shiguDeleteStart}
and a.shigu_delete <= #{params.shiguDeleteEnd}
and a.shigu_delete = #{params.shiguDelete}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.insert_time = #{params.insertTime}
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_uuid_number like CONCAT('%',#{params.yonghuUuidNumber},'%')
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
and yonghu.new_money >= #{params.newMoneyStart}
and yonghu.new_money <= #{params.newMoneyEnd}
and a.new_money = #{params.newMoney}
and a.yuangong_id IS NOT NULL
and yuangong.yuangong_uuid_number like CONCAT('%',#{params.yuangongUuidNumber},'%')
and yuangong.yuangong_name like CONCAT('%',#{params.yuangongName},'%')
and yuangong.yuangong_phone like CONCAT('%',#{params.yuangongPhone},'%')
and yuangong.yuangong_id_number like CONCAT('%',#{params.yuangongIdNumber},'%')
and yuangong.yuangong_email like CONCAT('%',#{params.yuangongEmail},'%')
order by a.${params.sort} ${params.order}
